import { text } from "@fortawesome/fontawesome-svg-core";
import { setOptions } from "leaflet";
import { useState } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import Button from "./Button";

const Spots = ({ spot, fetchSpotsById, toggleFavorite }) => {
  let text = "Add to favorites";
  let design =
    "bg-yellow-400 text-black text-sm rounded px-2 py-[0.15rem] hover:bg-yellow-500 ";
  const toggleBtn = () => {};
  return (
    <>
      {Array.isArray(spot)
        ? spot.map((spot) => (
            <Marker key={spot.id} position={[spot.lat, spot.long]}>
              <Popup>
                <h1>{spot.name}</h1>
                <h3>{spot.country}</h3>
                <p>
                  WIND PROBABILITY
                  <br />
                  {spot.probability}%
                </p>
                <p>
                  LONGITUDE:
                  <br />
                  {spot.long}
                </p>
                <p>
                  LATITUDE:
                  <br />
                  {spot.lat}
                </p>
                <p>
                  WHEN TO GO:
                  <br />
                  {spot.month}
                </p>
                {/* Favorite OR Delete favorite btn */}

                {spot.favourite ? (
                  <button
                    className="bg-red-400 text-black text-sm rounded px-2 py-[0.15rem] hover:bg-red-500 "
                    onClick={async () => {
                      await toggleFavorite(spot.id);
                    }}
                  >
                    Remove from favourite
                  </button>
                ) : (
                  <button
                    className="bg-yellow-400 text-black text-sm rounded px-2 py-[0.15rem] hover:bg-yellow-500 "
                    onClick={async () => {
                      await toggleFavorite(spot.id);
                      await fetchSpots();
                    }}
                  >
                    Add to favourite
                  </button>
                )}
              </Popup>
            </Marker>
          ))
        : null}
    </>
  );
};

export default Spots;
