import { useState } from "react";

const Table = ({ spot, setSpots }) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [order, setOrder] = useState("ASC");

  const sorting = (col) => {
    if (order === "ASC") {
      const sorted = [...spot].sort((a, b) =>
        a[col].toLowerCase() > b[col].toLowerCase() ? 1 : -1
      );
      setSpots(sorted);
      setOrder("DSC");
    }
    if (order === "DSC") {
      const sorted = [...spot].sort((a, b) =>
        a[col].toLowerCase() < b[col].toLowerCase() ? 1 : -1
      );
      setSpots(sorted);
      setOrder("ASC");
    }
  };
  return (
    <div className="container">
      <h1>Locations</h1>
      <input
        type="text"
        placeholder="Search..."
        onChange={(event) => {
          setSearchTerm(event.target.value);
        }}
      />
      <table>
        <thead>
          <tr>
            <th onClick={() => sorting("name")}>Name</th>
            <th>Country</th>
            <th>Latitude</th>
            <th>Longitude</th>
            <th>Wind Prob.</th>
            <th>When to go</th>
          </tr>
        </thead>
        <tbody>
          {/* Checking if API fetch is done */}
          {Array.isArray(spot)
            ? spot
                .filter((spot) => {
                  // Search function
                  if (searchTerm == "") {
                    return spot;
                  } else if (
                    // Searching location name
                    spot.name
                      .toLowerCase()
                      .includes(searchTerm.toLowerCase()) ||
                    // Searching location country
                    spot.country
                      .toLowerCase()
                      .includes(searchTerm.toLowerCase()) ||
                    // Searching month to go
                    spot.month.toLowerCase().includes(searchTerm.toLowerCase())
                  ) {
                    return spot;
                  }
                })
                .map((spot) => (
                  <tr key={spot.id}>
                    <td>{spot.name}</td>
                    <td>{spot.country}</td>
                    <td>{spot.lat}</td>
                    <td>{spot.long}</td>
                    <td>{spot.probability}%</td>
                    <td>{spot.month}</td>
                    <td />
                  </tr>
                ))
            : null}
        </tbody>
      </table>
    </div>
  );
};

Table.defaultProps = {
  currentSort: "default",
};
export default Table;
