import { useState } from "react";
import { BsArrowDownShort, BsArrowUpShort } from "react-icons/bs";

const Table = ({ spot, setSpots }) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [tempSpot, setTemp] = useState();
  const [order, setOrder] = useState("NRML");
  const [orderArrow, setArrow] = useState("");

  // Sorting function
  const sorting = (col) => {
    if (order === "ASC") {
      const sorted = [...spot].sort((a, b) =>
        a[col].toLowerCase() > b[col].toLowerCase() ? 1 : -1
      );
      setTemp(spot);
      setSpots(sorted);
      setArrow("up");
      setOrder("DSC");
    }
    if (order === "DSC") {
      const sorted = [...spot].sort((a, b) =>
        a[col].toLowerCase() < b[col].toLowerCase() ? 1 : -1
      );
      setSpots(sorted);
      setArrow("down");
      setOrder("NRML");
    }
    if (order === "NRML") {
      setSpots(spot);
      setOrder("ASC");
    }
  };

  return (
    <div className="container">
      <h1>Locations</h1>
      <input
        type="text"
        placeholder="Search..."
        onChange={(event) => {
          setSearchTerm(event.target.value);
        }}
      />
      <table>
        <thead>
          <tr>
            <th onClick={() => sorting("name")}>
              Name {orderArrow === "up" ? <BsArrowDownShort /> : null}
              {orderArrow === "down" ? <BsArrowUpShort /> : null}
            </th>
            <th onClick={() => sorting("country")}>
              Country {orderArrow === "up" ? <BsArrowDownShort /> : null}
              {orderArrow === "down" ? <BsArrowUpShort /> : null}
            </th>
            <th onClick={() => sorting("lat")}>Latitude</th>
            <th onClick={() => sorting("long")}>Longitude</th>
            <th onClick={() => sorting("probability")}>Wind Prob.</th>
            <th onClick={() => sorting("month")}>When to go</th>
          </tr>
        </thead>
        <tbody>
          {/* Checking if API fetch is done */}
          {Array.isArray(spot)
            ? spot
                .filter((spot) => {
                  // Search function
                  if (searchTerm == "") {
                    return spot;
                  } else if (
                    // Searching location name
                    spot.name
                      .toLowerCase()
                      .includes(searchTerm.toLowerCase()) ||
                    // Searching location country
                    spot.country
                      .toLowerCase()
                      .includes(searchTerm.toLowerCase()) ||
                    // Searching month to go
                    spot.month.toLowerCase().includes(searchTerm.toLowerCase())
                  ) {
                    return spot;
                  }
                })
                .map((spot) => (
                  <tr key={spot.id}>
                    <td>{spot.name}</td>
                    <td>{spot.country}</td>
                    <td>{spot.lat}</td>
                    <td>{spot.long}</td>
                    <td>{spot.probability}%</td>
                    <td>{spot.month}</td>
                    <td />
                  </tr>
                ))
            : null}
        </tbody>
      </table>
    </div>
  );
};

Table.defaultProps = {
  currentSort: "default",
};
export default Table;
