import { useState } from "react";
const AddSpot = ({ fetchSpots }) => {
  const [showModal, setShowModal] = useState(false);
  const [date, setDate] = useState();
  const [name, setLocation] = useState();
  const [country, setCountry] = useState();
  //  Uploading to API function

  const handleSubmit = (e) => {
    var arr = date.split("-");
    var months = [
      "January",
      "February",
      "March",
      "April",
      "May",
      "June",
      "July",
      "August",
      "September",
      "October",
      "November",
      "December",
    ];
    var month_index = parseInt(arr[1], 10) - 1;
    const month = months[month_index];

    const spot = { name, country, month };

    fetch("https://6255722e8646add390d70c22.mockapi.io/spot", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(spot),
    })
      .then(() => {
        console.log("New spot added");
      })
      .then(() => setShowModal(false));
  };

  return (
    <>
      <button
        className="bg-transparent hover:bg-blue-500 text-blue-700 font-semibold hover:text-white py-2 px-4 border border-blue-500 hover:border-transparent rounded"
        type="button"
        onClick={() => setShowModal(true)}
      >
        Add Spot
      </button>
      {showModal ? (
        <>
          <div className="w-100 justify-center items-center flex overflow-x-hidden overflow-y-auto fixed inset-0 z-50 outline-none focus:outline-none">
            <div className="relative w-auto my-6 mx-auto max-w-3xl">
              {/*content*/}
              <div className="border-0 rounded-lg shadow-lg relative flex flex-col w-full bg-white outline-none focus:outline-none">
                {/*header*/}
                <div className="flex items-start justify-between p-5 border-b border-solid border-slate-200 rounded-t">
                  <h3 className="text-3xl font-semibold">Add New Spot</h3>
                  <button
                    className="p-1 ml-auto bg-transparent border-0 text-black opacity-5 float-right text-3xl leading-none font-semibold outline-none focus:outline-none"
                    onClick={() => setShowModal(false)}
                  >
                    <span className="bg-transparent text-black opacity-5 h-6 w-6 text-2xl block outline-none focus:outline-none">
                      ×
                    </span>
                  </button>
                </div>
                {/*body*/}
                <div className="relative p-6 flex-auto">
                  <p className="p-2 font-semibold">Location Name</p>
                  <input
                    className="px-2 py-1"
                    type="text"
                    placeholder="Enter location name..."
                    onChange={(e) => {
                      let temp = e.target.value.toLocaleString("en-us", {
                        month: "long",
                      });
                      setLocation(temp);
                    }}
                  ></input>
                  <p className="p-2 font-semibold ">Country</p>
                  <input
                    className="px-2 py-1 "
                    type="text"
                    placeholder="Enter country..."
                    onChange={(e) => {
                      setCountry(e.target.value);
                    }}
                  ></input>
                  <p className="p-2 font-semibold">When to go </p>
                  {/* Date picker */}
                  <p className="font-semibold">from</p>
                  <input
                    type="date"
                    onChange={(e) => {
                      setDate(e.target.value);
                    }}
                  />
                  <p className="font-semibold">to</p>
                  <input type="date" />
                </div>

                {/*footer*/}
                <div className="flex items-center justify-end p-6 border-t border-solid border-slate-200 rounded-b">
                  <button
                    className="text-red-500 background-transparent font-bold uppercase px-6 py-2 text-sm outline-none focus:outline-none mr-1 mb-1 ease-linear transition-all duration-150"
                    type="button"
                    onClick={() => setShowModal(false)}
                  >
                    Close
                  </button>
                  <button
                    className="bg-emerald-500 text-white active:bg-emerald-600 font-bold uppercase text-sm px-6 py-3 rounded shadow hover:shadow-lg outline-none focus:outline-none mr-1 mb-1 ease-linear transition-all duration-150"
                    type="button"
                    onClick={() => {
                      handleSubmit();
                    }}
                  >
                    Save Changes
                  </button>
                </div>
              </div>
            </div>
          </div>
          <div className="opacity-25 fixed inset-0 z-40 bg-black"></div>
        </>
      ) : null}
    </>
  );
};

export default AddSpot;
