import { useState } from "react";

const Table = ({ spot }) => {
  const [searchTerm, setSearchTerm] = useState("");
  return (
    <div className="container">
      <h1>Locations</h1>
      <input
        type="text"
        placeholder="Search..."
        onChange={(event) => {
          setSearchTerm(event.target.value);
        }}
      />
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Country</th>
            <th>Latitude</th>
            <th>Longitude</th>
            <th>Wind Prob.</th>
            <th>When to go</th>
          </tr>
        </thead>
        <tbody>
          {Array.isArray(spot)
            ? spot
                .filter((spot) => {
                  if (searchTerm == "") {
                    return spot;
                  } else if (
                    spot.name.toLowerCase().includes(searchTerm.toLowerCase())
                  ) {
                    return spot;
                  }
                })
                .map((spot) => (
                  <tr key={spot.id}>
                    <td>{spot.name}</td>
                    <td>{spot.country}</td>
                    <td>{spot.lat}</td>
                    <td>{spot.long}</td>
                    <td>{spot.probability}%</td>
                    <td>{spot.month}</td>
                    <td />
                  </tr>
                ))
            : null}
        </tbody>
      </table>
    </div>
  );
};

export default Table;

