import { useState } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import Button from "./Button";

const Spots = ({ spot, fetchSpots }) => {
  const [favorite, setFavorite] = useState(false);
  // Toggle favorite in API
  const fetchFavorite = async () => {
    const res = await fetch(
      "https://6255722e8646add390d70c22.mockapi.io/favorite"
    );
    const data = await res.json();
    return data;
  };
  return (
    <>
      {Array.isArray(spot)
        ? spot.map((spot) => (
            <Marker key={spot.id} position={[spot.lat, spot.long]}>
              <Popup>
                <h1>{spot.name}</h1>
                <h3>{spot.country}</h3>
                <p>
                  WIND PROBABILITY
                  <br />
                  {spot.probability}%
                </p>
                <p>
                  LONGITUDE:
                  <br />
                  {spot.long}
                </p>
                <p>
                  LATITUDE:
                  <br />
                  {spot.lat}
                </p>
                <p>
                  WHEN TO GO:
                  <br />
                  {spot.month}
                </p>
                {/* Favorite OR Delete favorite btn */}
                <button
                  className="bg-yellow-400 text-black text-sm rounded px-2 py-[0.15rem] hover:bg-yellow-500  "
                  onClick={() => {}}
                >
                  Add to favorites
                </button>
              </Popup>
            </Marker>
          ))
        : null}
    </>
  );
};

export default Spots;
