const Button = ({ text, color }) => {
  const onClick = (e) => {
    if ((color = "yellow")) color = "red";
    else color = "yellow";
  };
  return (
    <button style={{ backgroundColor: color }} onClick={onClick}>
      {text}
    </button>
  );
};

Button.defaultProps = {
  text: `Add to favorites`,
};

export default Button;
