import Spots from "./components/Spots";
import Table from "./components/Table";
import AddSpot from "./components/AddSpot";
import "./App.css";
import "./components/Nav.css";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import React, { useState, useEffect } from "react";
import { data } from "autoprefixer";

function App() {
  const [spot, setSpots] = useState(null);

  // For - ADD SPOT -
  const [showModal, setShowModal] = useState(false);
  const [date, setDate] = useState();
  const [name, setLocation] = useState();
  const [country, setCountry] = useState();

  // Add spot modal state
  const [addSpot, setAddSpot] = useState(false);

  // Fetching Spots Data
  useEffect(() => {
    const getSpots = async () => {
      const spotsFromServer = await fetchSpots();

      setSpots(spotsFromServer);
    };
    getSpots();
  }, []);

  const fetchSpots = async () => {
    const res = await fetch("https://6255722e8646add390d70c22.mockapi.io/spot");
    const data = await res.json();
    return data;
  };

  // -- ADD SPOT --
  const handleSubmit = async (e) => {
    var arr = date.split("-");
    var months = [
      "January",
      "February",
      "March",
      "April",
      "May",
      "June",
      "July",
      "August",
      "September",
      "October",
      "November",
      "December",
    ];
    var month_index = parseInt(arr[1], 10) - 1;
    const month = months[month_index];

    const spot = { name, country, month };

    const result = await fetch(
      "https://6255722e8646add390d70c22.mockapi.io/spot",
      {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(spot),
      }
    );
    const data = await result
      .json()
      .then(() => setSpots([...spot, data]))
      .then(() => setShowModal(false));
  };

  return (
    <div>
      {/* Nav with logout, addspot props */}
      <div className="nav-container">
        <h1>Kite</h1>
        <div className="right-0 space-x-4">
          <AddSpot />
          <button className="bg-transparent hover:bg-red-500 text-blue-700 font-semibold hover:text-white py-2 px-4 border border-blue-500 hover:border-transparent rounded">
            Sign out
          </button>
        </div>
      </div>
      {/* Leaflet contaienr start*/}
      <div className="map">
        <MapContainer
          center={[0, 0]}
          zoom={2}
          bounds={([50, 50], [50, 50])}
          className="map"
        >
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            url="https://tiles.stadiamaps.com/tiles/outdoors/{z}/{x}/{y}{r}.png"
          />
          <Spots spot={spot} />
        </MapContainer>
      </div>
      {/* Leaftlet container end */}
      <Table spot={spot} />
    </div>
  );
}

export default App;
