import { useState, useEffect } from "react";
import { BsArrowDownShort, BsArrowUpShort } from "react-icons/bs";

const Table = ({ spot, setSpots, fetchSpots }) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [order, setOrder] = useState("ASC");
  const [orderArrow, setArrow] = useState("");

  useEffect(() => {
    const getSpots = async () => {
      const spotsFromServer = await fetchSpots();

      setSpots(spotsFromServer);
    };
    getSpots();
  }, []);
  // Sorting function
  const sorting = (col) => {
    if (order === "ASC") {
      const sorted = [...spot].sort((a, b) =>
        a[col].toLowerCase() > b[col].toLowerCase() ? 1 : -1
      );
      setSpots(sorted);
      setArrow("up");
      setOrder("DSC");
    }
    if (order === "DSC") {
      const sorted = [...spot].sort((a, b) =>
        a[col].toLowerCase() < b[col].toLowerCase() ? 1 : -1
      );
      setSpots(sorted);
      setArrow("down");
      setOrder("ASC");
    }
  };

  return (
    <div className="container">
      <h1 className="center">Locations</h1>
      <input
        type="text"
        placeholder="Search..."
        onChange={(event) => {
          setSearchTerm(event.target.value);
        }}
      />
      <div className="relative overflow-x-auto shadow-md sm:rounded-lg">
        <table className="w-full text-sm text-left text-gray-500 dark:text-gray-400">
          <thead className="text-xs text-gray-700 uppercase bg-gray-50 dark:bg-gray-700 dark:text-gray-400">
            <tr className="bg-gray-200">
              <th className="px-6 py-3" onClick={() => sorting("name")}>
                Name
              </th>
              <th className="px-6 py-3" onClick={() => sorting("country")}>
                Country
              </th>
              <th className="px-6 py-3" onClick={() => sorting("lat")}>
                Latitude
              </th>
              <th className="px-6 py-3" onClick={() => sorting("long")}>
                Longitude
              </th>
              <th className="px-6 py-3" onClick={() => sorting("probability")}>
                Wind Prob.
              </th>
              <th className="px-6 py-3" onClick={() => sorting("month")}>
                When to go
              </th>
            </tr>
          </thead>
          <tbody className="bg-white border-b dark:bg-gray-800 dark:border-gray-700 hover:bg-gray-50 dark:hover:bg-gray-600">
            {/* Checking if API fetch is done */}
            {Array.isArray(spot)
              ? spot
                  .filter((spot) => {
                    // Search function
                    if (searchTerm == "") {
                      return spot;
                    } else if (
                      // Searching location name
                      spot.name
                        .toLowerCase()
                        .includes(searchTerm.toLowerCase()) ||
                      // Searching location country
                      spot.country
                        .toLowerCase()
                        .includes(searchTerm.toLowerCase()) ||
                      // Searching month to go
                      spot.month
                        .toLowerCase()
                        .includes(searchTerm.toLowerCase())
                    ) {
                      return spot;
                    }
                  })
                  .map((spot) => (
                    <tr
                      key={spot.id}
                      className={
                        spot.favourite ? "bg-yellow-300" : "bg-gray-200"
                      }
                    >
                      <th className="px-6 py-4 font-medium text-gray-900 dark:text-white whitespace-nowrap">
                        {spot.name}
                      </th>
                      <td className="px-6 py-4">{spot.country}</td>
                      <td className="px-6 py-4">{spot.lat}</td>
                      <td className="px-6 py-4">{spot.long}</td>
                      <td className="px-6 py-4">{spot.probability}%</td>
                      <td className="px-6 py-4">{spot.month}</td>
                    </tr>
                  ))
              : null}
          </tbody>
        </table>
      </div>
    </div>
  );
};

Table.defaultProps = {
  currentSort: "default",
};
export default Table;
