import Nav from "./components/Nav";
import Spots from "./components/Spots";
import Table from "./components/Table";
import Loginpage from "./components/Loginpage";
import "./App.css";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import React, { useState, useEffect } from "react";

function App() {
  const [spot, setSpots] = useState(null);

  //Login modal state
  const [openModal, setOpenModal] = useState("true");
  // Logout modal state
  const [outModal, setOutModal] = useState(false);
  // Add spot modal state
  const [addSpot, setAddSpot] = useState(false);
  //
  const [fetch, setFetch] = useState();
  // Fetching Spots Data
  useEffect(() => {
    const getSpots = async () => {
      const spotsFromServer = await fetchSpots();

      setSpots(spotsFromServer);
    };
    getSpots();
  }, []);

  const fetchSpots = async () => {
    const res = await fetch("https://6255722e8646add390d70c22.mockapi.io/spot");
    const data = await res.json();
    return data;
  };

  return (
    <div>
      {/* Login modal */}
      {openModal && <Loginpage toggleModal={setOpenModal} />}
      {/* Nav with logout, addspot props */}
      <Nav
        openModal={setOpenModal}
        addSpot={setAddSpot}
        fetch={this.fetchSpots}
      />
      {/* Leaflet contaienr start*/}
      <div className="map">
        {!openModal && (
          <MapContainer
            center={[0, 0]}
            zoom={2}
            bounds={([50, 50], [50, 50])}
            className="map"
          >
            <TileLayer
              attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
              url="https://tiles.stadiamaps.com/tiles/outdoors/{z}/{x}/{y}{r}.png"
            />
            <Spots spot={spot} />
          </MapContainer>
        )}
      </div>
      {/* Leaftlet container end */}
      <Table spot={spot} />
    </div>
  );
}

export default App;
