import { text } from "@fortawesome/fontawesome-svg-core";
import { useState } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import Button from "./Button";

const Spots = ({ spot, fetchSpots, toggleFavorite }) => {
  const [favorite, setFavorite] = useState(false);
  let text = "Add to favorites";
  let design =
    "bg-yellow-400 text-black text-sm rounded px-2 py-[0.15rem] hover:bg-yellow-500 ";
  return (
    <>
      {Array.isArray(spot)
        ? spot.map((spot) => (
            <Marker key={spot.id} position={[spot.lat, spot.long]}>
              <Popup>
                <h1>{spot.name}</h1>
                <h3>{spot.country}</h3>
                <p>
                  WIND PROBABILITY
                  <br />
                  {spot.probability}%
                </p>
                <p>
                  LONGITUDE:
                  <br />
                  {spot.long}
                </p>
                <p>
                  LATITUDE:
                  <br />
                  {spot.lat}
                </p>
                <p>
                  WHEN TO GO:
                  <br />
                  {spot.month}
                </p>
                {/* Favorite OR Delete favorite btn */}
                <button
                  className={design}
                  onClick={() => {
                    toggleFavorite(spot.id);
                    console.log(spot.favourite);
                    // text = {spot.favourite
                    //   ? " Remove from favourites "
                    //   : "Add to favourites"}
                    // design = {
                    //   spot.favourite
                    //     ? "bg-red-400 text-black text-sm rounded px-2 py-[0.15rem] hover:bg-red-500 "
                    //     : "bg-yellow-400 text-black text-sm rounded px-2 py-[0.15rem] hover:bg-yellow-500 "
                    // }
                  }}
                >
                  {text}
                </button>
              </Popup>
            </Marker>
          ))
        : null}
    </>
  );
};

export default Spots;
