import { useState } from "react";

const Table = ({ spot, currentSort }) => {
  const [searchTerm, setSearchTerm] = useState("");
  const sortTypes = {
    up: {
      class: "sort-up",
      fn: (a, b) => a.probability - b.probability,
    },
    down: {
      class: "sort-down",
      fn: (a, b) => b.probability - a.probability,
    },
    default: {
      class: "sort",
      fn: (a, b) => a,
    },
  };

  onSortChange = () => {
    const { currentSort } = this.state;
    let nextSort;

    if (currentSort === "down") nextSort = "up";
    else if (currentSort === "up") nextSort = "default";
    else if (currentSort === "default") nextSort = "down";

    this.setState({
      currentSort: nextSort,
    });
  };
  return (
    <div className="container">
      <h1>Locations</h1>
      <input
        type="text"
        placeholder="Search..."
        onChange={(event) => {
          setSearchTerm(event.target.value);
        }}
      />
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Country</th>
            <th>Latitude</th>
            <th>Longitude</th>
            <th>
              Wind Prob.
              <button onClick={this.onSortChange}></button>
            </th>
            <th>When to go</th>
          </tr>
        </thead>
        <tbody>
          {/* Checking if API fetch is done */}
          {Array.isArray(spot)
            ? spot
                .filter((spot) => {
                  // Search function
                  if (searchTerm == "") {
                    return spot;
                  } else if (
                    // Searching location name
                    spot.name
                      .toLowerCase()
                      .includes(searchTerm.toLowerCase()) ||
                    // Searching location country
                    spot.country
                      .toLowerCase()
                      .includes(searchTerm.toLowerCase()) ||
                    // Searching month to go
                    spot.month.toLowerCase().includes(searchTerm.toLowerCase())
                  ) {
                    return spot;
                  }
                })
                .map((spot) => (
                  <tr key={spot.id}>
                    <td>{spot.name}</td>
                    <td>{spot.country}</td>
                    <td>{spot.lat}</td>
                    <td>{spot.long}</td>
                    <td>{spot.probability}%</td>
                    <td>{spot.month}</td>
                    <td />
                  </tr>
                ))
            : null}
        </tbody>
      </table>
    </div>
  );
};

Table.defaultProps = {
  currentSort: "default",
};
export default Table;
