import { useState, useEffect } from "react";
import { BsArrowDownShort, BsArrowUpShort } from "react-icons/bs";

const Table = ({ spot, setSpots, fetchSpots }) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [order, setOrder] = useState("ASC");
  const [orderArrow, setArrow] = useState("");

  useEffect(() => {
    const getSpots = async () => {
      const spotsFromServer = await fetchSpots();

      setSpots(spotsFromServer);
    };
    getSpots();
  }, []);
  // Sorting function
  const sorting = (col) => {
    if (order === "ASC") {
      const sorted = [...spot].sort((a, b) =>
        a[col].toLowerCase() > b[col].toLowerCase() ? 1 : -1
      );
      setSpots(sorted);
      setArrow("up");
      setOrder("DSC");
    }
    if (order === "DSC") {
      const sorted = [...spot].sort((a, b) =>
        a[col].toLowerCase() < b[col].toLowerCase() ? 1 : -1
      );
      setSpots(sorted);
      setArrow("down");
      setOrder("ASC");
    }
  };

  return (
    <div className="container">
      <h1 className="center">Locations</h1>
      <input
        type="text"
        placeholder="Search..."
        onChange={(event) => {
          setSearchTerm(event.target.value);
        }}
      />
      <table className="table-auto">
        <thead className="bg-gray-500">
          <tr>
            <th onClick={() => sorting("name")}>Name</th>
            <th onClick={() => sorting("country")}>Country</th>
            <th onClick={() => sorting("lat")}>Latitude</th>
            <th onClick={() => sorting("long")}>Longitude</th>
            <th onClick={() => sorting("probability")}>Wind Prob.</th>
            <th onClick={() => sorting("month")}>When to go</th>
          </tr>
        </thead>
        <tbody>
          {/* Checking if API fetch is done */}
          {Array.isArray(spot)
            ? spot
                .filter((spot) => {
                  // Search function
                  if (searchTerm == "") {
                    return spot;
                  } else if (
                    // Searching location name
                    spot.name
                      .toLowerCase()
                      .includes(searchTerm.toLowerCase()) ||
                    // Searching location country
                    spot.country
                      .toLowerCase()
                      .includes(searchTerm.toLowerCase()) ||
                    // Searching month to go
                    spot.month.toLowerCase().includes(searchTerm.toLowerCase())
                  ) {
                    return spot;
                  }
                })
                .map((spot) => (
                  <tr
                    key={spot.id}
                    className={spot.favourite ? "bg-yellow-300" : "bg-gray-200"}
                  >
                    <td>{spot.name}</td>
                    <td>{spot.country}</td>
                    <td>{spot.lat}</td>
                    <td>{spot.long}</td>
                    <td>{spot.probability}%</td>
                    <td>{spot.month}</td>
                    <td />
                  </tr>
                ))
            : null}
        </tbody>
      </table>
    </div>
  );
};

Table.defaultProps = {
  currentSort: "default",
};
export default Table;
